let menu=document.querySelector("#menu-btn"),header=document.querySelector(".header");menu.onclick=()=>{menu.classList.toggle("fa-times"),header.classList.toggle("active")},window.onscroll=()=>{menu.classList.remove("fa-times"),header.classList.remove("active")};let themeToggler=document.querySelector("#theme-toggler");themeToggler.onclick=()=>{themeChenge()};const theme=localStorage.getItem("theme");function themeChenge(){themeToggler.classList.toggle("fa-sun"),themeToggler.classList.contains("fa-sun")?(localStorage.setItem("theme","moon"),document.body.classList.add("active")):(localStorage.setItem("theme","sun"),document.body.classList.remove("active"))}theme.includes("moon")&&themeChenge();