let menuBtn=document.querySelector("#theme-toggler-1");menuBtn.onclick=()=>{history.back()};let themeToggler=document.querySelector("#theme-toggler");themeToggler.onclick=()=>{themeChenge()};const theme=localStorage.getItem("theme");function themeChenge(){themeToggler.classList.toggle("fa-sun"),themeToggler.classList.contains("fa-sun")?(localStorage.setItem("theme","moon"),document.body.classList.add("active")):(localStorage.setItem("theme","sun"),document.body.classList.remove("active"))}theme.includes("moon")&&themeChenge();