let menu=document.querySelector("#menu-btn"),header=document.querySelector(".header");menu.onclick=()=>{menu.classList.toggle("fa-times"),header.classList.toggle("active")},window.onscroll=()=>{menu.classList.remove("fa-times"),header.classList.remove("active")};let themeToggler=document.querySelector("#theme-toggler");themeToggler.onclick=()=>{themeToggler.classList.toggle("fa-sun"),themeToggler.classList.contains("fa-sun")?document.body.classList.add("active"):document.body.classList.remove("active")};